import React from 'react';
import { IncomingMessage } from 'http';
import { Client } from '../prismic';

export default class extends React.Component<{ home: any, configuration: any, pageURL: string, error: any }> {

	static async getInitialProps({ req, res, query, pathname }: { req: IncomingMessage, res: any, query: any, pathname: string }) {
		try {
			const home = await Client(req).getSingle('homepage', { lang: query.language });

			const pageURL = req ? req.protocol + '://' + req.headers.host + req.url : window.location.origin + pathname;

			if (!home) {
				res.statusCode = 404;

				const error = {
					statusCode: 404
				};

				return { error }
			}

			return { home, pageURL };
		} catch (error) {
			return { error: true };
		}
	}

	renderSlices = (slices: Slice[]) =>
		slices.map((slice: any, index: number) => {
			let key = slice.slice_type + index;
			switch (slice.slice_type) {
				case 'content_teaser':
					return <ContentTeaser
						title={slice.primary.title}
						intro={slice.primary.intro}
						linkLabel={slice.primary.link_label}
						linkUrl={slice.primary.link}
						image={slice.primary.image}
						key={key}
					/>;
				case 'select_flokes_section':
					return <FlokeSection
						title={slice.primary.title}
						link={linkResolver(slice.primary.text_block_link)}
						description={slice.primary.description}
						linkLabel={slice.primary.link_label}
						flokeList={slice.items.slice(0, 3)}
						key={key}
					/>;
				case 'image':
					return <ImageSection
						image={slice.primary.image}
						key={key}
					/>;
				case 'content_reference':
					return <ContentReference
						title={slice.primary.title}
						description={slice.primary.description}
						elementsList={slice.items}
						buttonLabel={slice.primary.button_label}
						key={key}
					/>;
				default: return;
			}
		})

	render() {
        <div>
            index.html
        </div>
	}
}

